import React, { useState } from 'react';
import axios from 'axios';

function ClientForm({ onBack }) {
  const [headerData, setHeaderData] = useState({
    type: '',
    requestNum: '',
    requestedBy: '',
    workNum: '',
    workSlNo: '',
    natureOfWork: '',
    siteName: '',
  });

  const [rows, setRows] = useState([
    { slNo: '', particulars: '', size: '', quantity: '', unit: '' },
  ]);

  const handleHeaderChange = (e) => {
    setHeaderData({ ...headerData, [e.target.name]: e.target.value });
  };

  const handleRowChange = (index, field, value) => {
    const updated = [...rows];
    updated[index][field] = value;
    setRows(updated);
  };

  const addRow = () => {
    setRows([...rows, { slNo: '', particulars: '', size: '', quantity: '', unit: '' }]);
  };

  const removeRow = (index) => {
    const updated = [...rows];
    updated.splice(index, 1);
    setRows(updated);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const payload = {
        ...headerData,
        materials: rows,
      };
      await axios.post('http://localhost:5000/api/submit-form', payload);
      alert('✅ Form submitted successfully!');
    } catch (err) {
      console.error(err);
      alert('❌ Submission failed.');
    }
  };

  const inputStyle = {
    width: '150px',
    padding: '8px',
    boxSizing: 'border-box',
  };

  return (
    <div style={{ padding: '2rem' }}>
      <h2>📋 Client Material Request Form</h2>

      <div style={{ overflowX: 'auto' }}>
        <form onSubmit={handleSubmit}>
          {/* Fixed Header Fields */}
          <table border="1" cellPadding="8" style={{ borderCollapse: 'collapse', minWidth: '1000px' }}>
            <thead style={{ backgroundColor: '#f0f0f0' }}>
              <tr>
                <th>TYPE</th>
                <th>REQUEST NUM</th>
                <th>REQUESTED BY</th>
                <th>WORK NUMBER</th>
                <th>WORK SL.NO</th>
                <th>NATURE OF WORK</th>
                <th>SITE NAME</th>
              </tr>
            </thead>
            <tbody>
              <tr>
                {Object.keys(headerData).map((key) => (
                  <td key={key}>
                    <input
                      type="text"
                      name={key}
                      value={headerData[key]}
                      onChange={handleHeaderChange}
                      style={inputStyle}
                      required
                    />
                  </td>
                ))}
              </tr>
            </tbody>
          </table>

          {/* Addable Material Rows */}
          <table border="1" cellPadding="8" style={{ borderCollapse: 'collapse', minWidth: '1000px', marginTop: '1rem' }}>
            <thead style={{ backgroundColor: '#f0f0f0' }}>
              <tr>
                <th>SL.NO</th>
                <th>PARTICULARS</th>
                <th>SIZE</th>
                <th>QUANTITY</th>
                <th>UNIT</th>
                <th>🗑️</th>
              </tr>
            </thead>
            <tbody>
              {rows.map((row, idx) => (
                <tr key={idx}>
                  {Object.keys(row).map((key) => (
                    <td key={key}>
                      <input
                        type="text"
                        value={row[key]}
                        onChange={(e) => handleRowChange(idx, key, e.target.value)}
                        style={inputStyle}
                        required
                      />
                    </td>
                  ))}
                  <td>
                    {rows.length > 1 && (
                      <button type="button" onClick={() => removeRow(idx)}>❌</button>
                    )}
                  </td>
                </tr>
              ))}
            </tbody>
          </table>

          <div style={{ marginTop: '1rem' }}>
            <button type="button" onClick={addRow}>➕ Add Row</button>{' '}
            <button type="submit">✅ Submit</button>{' '}
            <button type="button" onClick={onBack}>⬅️ Back</button>
          </div>
        </form>
      </div>
    </div>
  );
}

export default ClientForm;
