import React, { useState } from 'react';
import axios from 'axios';

function ClientForm({ onBack }) {
  const [rows, setRows] = useState([
    {
      slNo: '',
      particulars: '',
      size: '',
      quantity: '',
      unit: '',
    },
  ]);

  const [headerData, setHeaderData] = useState({
    type: '',
    requestNum: '',
    requestedBy: '',
    workNum: '',
    workSlNo: '',
    natureOfWork: '',
    siteName: '',
  });

  const handleHeaderChange = (e) => {
    setHeaderData({ ...headerData, [e.target.name]: e.target.value });
  };

  const handleRowChange = (index, field, value) => {
    const updated = [...rows];
    updated[index][field] = value;
    setRows(updated);
  };

  const addRow = () => {
    setRows([...rows, { slNo: '', particulars: '', size: '', quantity: '', unit: '' }]);
  };

  const removeRow = (index) => {
    const updated = [...rows];
    updated.splice(index, 1);
    setRows(updated);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const payload = {
      ...headerData,
      rows,
    };

    try {
      await axios.post('http://localhost:5000/api/submit-form', payload);
      alert('✅ Submitted and written to Excel successfully!');
    } catch (err) {
      console.error(err);
      alert('❌ Failed to submit form.');
    }
  };

  return (
    <div style={{ padding: '2rem' }}>
      <h2>📋 Client Material Request Form</h2>

      <div style={{ overflowX: 'auto', whiteSpace: 'nowrap' }}>
        <form onSubmit={handleSubmit}>
          <table border="1" cellPadding="10" style={{ borderCollapse: 'collapse', minWidth: '1200px' }}>
            <thead style={{ background: '#f0f0f0' }}>
              <tr>
                <th>TYPE</th>
                <th>REQUEST NUM</th>
                <th>REQUESTED BY</th>
                <th>WORK NUMBER</th>
                <th>WORK SL.NO</th>
                <th>NATURE OF WORK</th>
                <th>SITE NAME</th>
                <th>SL.NO</th>
                <th>PARTICULARS</th>
                <th>SIZE</th>
                <th>QUANTITY</th>
                <th>UNIT</th>
                <th>🗑️</th>
              </tr>
            </thead>
            <tbody>
              {/* Read-only header row */}
              <tr>
                {Object.keys(headerData).map((key) => (
                  <td key={key}>
                    <input
                      type="text"
                      name={key}
                      value={headerData[key]}
                      onChange={handleHeaderChange}
                      style={{ width: '100%', minWidth: '150px' }}
                      required
                    />
                  </td>
                ))}
                <td colSpan={6}></td>
              </tr>

              {/* Addable rows */}
              {rows.map((row, idx) => (
                <tr key={idx}>
                  <td>
                    <input
                      type="text"
                      value={row.slNo}
                      onChange={(e) => handleRowChange(idx, 'slNo', e.target.value)}
                      style={{ width: '100%', minWidth: '100px' }}
                      required
                    />
                  </td>
                  <td>
                    <input
                      type="text"
                      value={row.particulars}
                      onChange={(e) => handleRowChange(idx, 'particulars', e.target.value)}
                      style={{ width: '100%', minWidth: '150px' }}
                      required
                    />
                  </td>
                  <td>
                    <input
                      type="text"
                      value={row.size}
                      onChange={(e) => handleRowChange(idx, 'size', e.target.value)}
                      style={{ width: '100%', minWidth: '100px' }}
                      required
                    />
                  </td>
                  <td>
                    <input
                      type="text"
                      value={row.quantity}
                      onChange={(e) => handleRowChange(idx, 'quantity', e.target.value)}
                      style={{ width: '100%', minWidth: '100px' }}
                      required
                    />
                  </td>
                  <td>
                    <input
                      type="text"
                      value={row.unit}
                      onChange={(e) => handleRowChange(idx, 'unit', e.target.value)}
                      style={{ width: '100%', minWidth: '100px' }}
                      required
                    />
                  </td>
                  <td>
                    {rows.length > 1 && (
                      <button type="button" onClick={() => removeRow(idx)}>❌</button>
                    )}
                  </td>
                </tr>
              ))}
            </tbody>
          </table>

          <div style={{ marginTop: '1rem' }}>
            <button type="button" onClick={addRow}>➕ Add Row</button>{' '}
            <button type="submit">✅ Submit</button>{' '}
            <button type="button" onClick={onBack}>⬅️ Back</button>
          </div>
        </form>
      </div>
    </div>
  );
}

export default ClientForm;
