import React, { useState } from 'react';
import axios from 'axios';

function ClientForm({ onBack }) {
  const [headerData, setHeaderData] = useState({
    type: '',
    requestNum: '',
    requestedBy: '',
    workNum: '',
    workSlNo: '',
    natureOfWork: '',
    siteName: '',
  });

  const [rows, setRows] = useState([
    {
      slNo: '',
      particulars: '',
      size: '',
      quantity: '',
      unit: '',
    },
  ]);

  const handleHeaderChange = (e) => {
    setHeaderData({ ...headerData, [e.target.name]: e.target.value });
  };

  const handleRowChange = (index, field, value) => {
    const updated = [...rows];
    updated[index][field] = value;
    setRows(updated);
  };

  const addRow = () => {
    setRows([...rows, { slNo: '', particulars: '', size: '', quantity: '', unit: '' }]);
  };

  const removeRow = (index) => {
    const updated = [...rows];
    updated.splice(index, 1);
    setRows(updated);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const payload = {
      ...headerData,
      rows,
    };

    try {
      await axios.post('http://localhost:5000/api/submit-form', payload);
      alert('‚úÖ Submitted and written to Excel successfully!');
    } catch (err) {
      console.error(err);
      alert('‚ùå Failed to submit form.');
    }
  };

  return (
    <div style={{ padding: '2rem', maxWidth: '1200px', margin: '0 auto' }}>
      <h2 style={{ marginBottom: '1rem' }}>üìã Client Material Request Form</h2>

      {/* Metadata Section */}
      <div style={{ display: 'grid', gridTemplateColumns: 'repeat(3, 1fr)', gap: '1rem', marginBottom: '2rem' }}>
        {Object.entries(headerData).map(([key, value]) => (
          <div key={key}>
            <label style={{ fontWeight: 'bold' }}>{key}</label>
            <input
              type="text"
              name={key}
              value={value}
              onChange={handleHeaderChange}
              required
              style={{ width: '100%', padding: '0.5rem', borderRadius: '4px' }}
            />
          </div>
        ))}
      </div>

      {/* Table for Items */}
      <form onSubmit={handleSubmit}>
        <table style={{ width: '100%', borderCollapse: 'collapse' }}>
          <thead style={{ backgroundColor: '#f0f0f0' }}>
            <tr>
              <th>SL.NO</th>
              <th>PARTICULARS</th>
              <th>SIZE</th>
              <th>QUANTITY</th>
              <th>UNIT</th>
              <th>üóëÔ∏è</th>
            </tr>
          </thead>
          <tbody>
            {rows.map((row, idx) => (
              <tr key={idx}>
                <td>
                  <input
                    type="text"
                    value={row.slNo}
                    onChange={(e) => handleRowChange(idx, 'slNo', e.target.value)}
                    required
                    style={{ width: '100%' }}
                  />
                </td>
                <td>
                  <input
                    type="text"
                    value={row.particulars}
                    onChange={(e) => handleRowChange(idx, 'particulars', e.target.value)}
                    required
                    style={{ width: '100%' }}
                  />
                </td>
                <td>
                  <input
                    type="text"
                    value={row.size}
                    onChange={(e) => handleRowChange(idx, 'size', e.target.value)}
                    required
                    style={{ width: '100%' }}
                  />
                </td>
                <td>
                  <input
                    type="text"
                    value={row.quantity}
                    onChange={(e) => handleRowChange(idx, 'quantity', e.target.value)}
                    required
                    style={{ width: '100%' }}
                  />
                </td>
                <td>
                  <input
                    type="text"
                    value={row.unit}
                    onChange={(e) => handleRowChange(idx, 'unit', e.target.value)}
                    required
                    style={{ width: '100%' }}
                  />
                </td>
                <td>
                  {rows.length > 1 && (
                    <button type="button" onClick={() => removeRow(idx)}>‚ùå</button>
                  )}
                </td>
              </tr>
            ))}
          </tbody>
        </table>

        {/* Action Buttons */}
        <div style={{ marginTop: '1rem' }}>
          <button type="button" onClick={addRow} style={{ marginRight: '1rem' }}>‚ûï Add Row</button>
          <button type="submit" style={{ marginRight: '1rem' }}>‚úÖ Submit</button>
          <button type="button" onClick={onBack}>‚¨ÖÔ∏è Back</button>
        </div>
      </form>
    </div>
  );
}

export default ClientForm;
