import React, { useState } from 'react';
import axios from 'axios';

function ClientForm({ onBack }) {
  const [header] = useState({
    type: 'C', // Or use a state setter if you want to allow change
    requestNum: 'REQ-001',
    requestedBy: 'John Doe',
    workNum: 'W-1234',
    workSlNo: '1',
    natureOfWork: 'Excavation',
    siteName: 'Site Alpha',
  });

  const [rows, setRows] = useState([
    { slNo: '', particulars: '', size: '', quantity: '', unit: '' }
  ]);

  const handleRowChange = (index, field, value) => {
    const updatedRows = [...rows];
    updatedRows[index][field] = value;
    setRows(updatedRows);
  };

  const addRow = () => {
    setRows([...rows, { slNo: '', particulars: '', size: '', quantity: '', unit: '' }]);
  };

  const removeRow = (index) => {
    const updated = [...rows];
    updated.splice(index, 1);
    setRows(updated);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const payload = { ...header, materials: rows };

    try {
      await axios.post('http://localhost:5000/api/submit-form', payload);
      alert('✅ Form submitted successfully!');
    } catch (err) {
      console.error(err);
      alert('❌ Submission failed.');
    }
  };

  return (
    <div style={{ padding: '2rem' }}>
      <h2>📋 Client Material Request Form</h2>

      <div style={{ marginBottom: '1rem', lineHeight: '1.8' }}>
        <div><strong>TYPE:</strong> {header.type}</div>
        <div><strong>REQUEST NUM:</strong> {header.requestNum}</div>
        <div><strong>REQUESTED BY:</strong> {header.requestedBy}</div>
        <div><strong>WORK NUMBER:</strong> {header.workNum}</div>
        <div><strong>WORK SL.NO:</strong> {header.workSlNo}</div>
        <div><strong>NATURE OF WORK:</strong> {header.natureOfWork}</div>
        <div><strong>SITE NAME:</strong> {header.siteName}</div>
      </div>

      <form onSubmit={handleSubmit}>
        <table border="1" cellPadding="8" style={{ width: '100%', borderCollapse: 'collapse' }}>
          <thead style={{ backgroundColor: '#f0f0f0' }}>
            <tr>
              <th>SL.NO</th>
              <th>PARTICULARS</th>
              <th>SIZE</th>
              <th>QUANTITY</th>
              <th>UNIT</th>
              <th>🗑️</th>
            </tr>
          </thead>
          <tbody>
            {rows.map((row, idx) => (
              <tr key={idx}>
                <td>
                  <input
                    type="text"
                    value={row.slNo}
                    onChange={(e) => handleRowChange(idx, 'slNo', e.target.value)}
                    required
                  />
                </td>
                <td>
                  <input
                    type="text"
                    value={row.particulars}
                    onChange={(e) => handleRowChange(idx, 'particulars', e.target.value)}
                    required
                  />
                </td>
                <td>
                  <input
                    type="text"
                    value={row.size}
                    onChange={(e) => handleRowChange(idx, 'size', e.target.value)}
                    required
                  />
                </td>
                <td>
                  <input
                    type="text"
                    value={row.quantity}
                    onChange={(e) => handleRowChange(idx, 'quantity', e.target.value)}
                    required
                  />
                </td>
                <td>
                  <input
                    type="text"
                    value={row.unit}
                    onChange={(e) => handleRowChange(idx, 'unit', e.target.value)}
                    required
                  />
                </td>
                <td>
                  {rows.length > 1 && (
                    <button type="button" onClick={() => removeRow(idx)}>❌</button>
                  )}
                </td>
              </tr>
            ))}
          </tbody>
        </table>

        <div style={{ marginTop: '1rem' }}>
          <button type="button" onClick={addRow}>➕ Add Row</button>{' '}
          <button type="submit">✅ Submit</button>{' '}
          <button type="button" onClick={onBack}>⬅️ Back</button>
        </div>
      </form>
    </div>
  );
}

export default ClientForm;
